SET DATEFORMAT dmy;
USE BDPM;
BEGIN TRANSACTION;










DECLARE @r TABLE( Ligne BigInt Identity(1,1) PRIMARY KEY, Y BigInt )
INSERT @r( Y )
  SELECT id
    FROM [dbo].[medicaments]
    WHERE ( BDPM.dbo.check_if_medicaments_exists(id) = 0 )
 
DECLARE @i BigInt, @y BigInt
WHILE Exists( SELECT * FROM @r ) BEGIN
  SELECT TOP 1 @i = Ligne, @y = Y FROM @r
  DELETE @r WHERE Ligne = @i
  DELETE FROM [dbo].[medicaments] WHERE id = @i
END



DECLARE 
    @cur_denomination VARCHAR(MAX), 
    @cur_cip13  BIGINT = 0,
	@nb_lignes	INT = 0;
DECLARE c1_cursor CURSOR  
    FOR select QQCHOSE.denomination, QQCHOSE.cip13
			from (select CCB.cip13 as cip13, CONCAT ( CB.denomination,' ',CCB.libelle_prez,' [', CB.administration, ']') as denomination, CB.etat_com
						FROM  LENOVO.[CIS].[dbo].[cis_bdpm] CB, LENOVO.[CIS].[dbo].[cis_cip_bdpm] CCB
						WHERE CB.cis = CCB.cis) as QQCHOSE
			WHERE CAST(QQCHOSE.etat_com AS nVARCHAR(255)) = 'Commercialisée'
			order by QQCHOSE.denomination
OPEN c1_cursor  
FETCH NEXT FROM c1_cursor INTO @cur_denomination, @cur_cip13;
WHILE @@FETCH_STATUS = 0  
BEGIN
	IF EXISTS(SELECT 1 FROM [dbo].[medicaments] WHERE [dbo].[medicaments].id = @cur_cip13)
		BEGIN
			UPDATE [dbo].[medicaments] SET nom=@cur_denomination WHERE [dbo].[medicaments].id = @cur_cip13
		END
	ELSE
		BEGIN
			SET IDENTITY_INSERT [dbo].[medicaments] ON
		END
	FETCH NEXT FROM c1_cursor INTO @cur_denomination, @cur_cip13;
END   
CLOSE c1_cursor;  
DEALLOCATE c1_cursor
COMMIT;
GO
